Ship targeted.script:
{Log("EVENT: Ship targeted")}

{_ Triggered when the player selects a target }

{_ event Parameters:   targetlocked - bool (ie false when losing target) }
{_ If target locked:   name (of ship), scanstage (ie 0, 1, 2, 3) }
{_ If Scan stage >= 1: name (of pilot), rank (of pilot) }
{_ If scan stage >= 2: shieldhealth, hullhealth (both 0-100) }
{_ If scan stage >= 3: faction, legalstatus (ie clean, wanted, etc), bounty (if appicable), }
{_                     subsystem (ie FSD, power plant, etc), subsystemhealth (0-100) }

{_ Context }
{SetState('eddi_context_last_subject', 'ship')}
{SetState('eddi_context_last_action', 'targeted')}

{if event.targetlocked = true	: {SetState('ship_targetlocked', true)}
|else							: {SetState('ship_targetlocked', false)}
}

{if ship.role = "Auftanken" || ship.role = "Bergbau" || ship.role = "Erkundung" 
|| ship.role = "Handel" || ship.role = "Journalismus" || ship.role = "MultiCrew" 
|| ship.role = "Racing" || ship.role = "Schmuggel" || ship.role = "Suche und Rettung" 
|| ship.role = "Taxi" || ship.role = "Wissenschaft" : {set role to "friedlich"}
}

{  if state.target_ansagen = 0 :	{_ keine Ansage _}
|elif state.target_ansagen = 1 : 	{_ keine Ansage nur bei oben aufgeführten Schiffsrollen _}
	{if role = "friedlich" : 
	|else:
		{if event.targetlocked:
			{if slice(event.name, 15) = cmdr.name:
				{if status.vehicle = "SRV":
					{if state.stimme_srv:
    					{set response to:
    						{OneOf("Das ist unser Schiff", "aha, da ist unser Schiff", 
    						"welch ein schönes Schiff, es gehört uns")}}
    						{Voice(response, state.stimme_srv)}
					|else:
    					{OneOf("Das ist unser Schiff", "aha, da ist unser Schiff", 
    					"welch ein schönes Schiff, es gehört uns")}							
					}
				|elif status.vehicle = "Fighter":
					{if state.stimme_fighter:
						{set response to:
    						{OneOf("Das ist unser Schiff", "aha, da ist unser Schiff", 
    						"welch ein schönes Schiff, es gehört uns")}}
    						{Voice(response, state.stimme_fighter)}
    				|else:
    					{OneOf("Das ist unser Schiff", "aha, da ist unser Schiff", 
    					"welch ein schönes Schiff, es gehört uns")}
    				}
				|else:
				}
			|else:
				{if event.scanstage > 2 && !event.subsystem:
					{set legalstatus to event.legalstatus}
					{if legalstatus = "Gesetzlos" || legalstatus = "Feindlich" 
					|| legalstatus = "Gesuchter Feind" || legalstatus = "Gesucht" || legalstatus = "Haftbefehl":
						{set wanted_list to split(state.eddi_context_wanted_list, "+=")}
						{if find(wanted_list, event.name) < 0:
    						{if status.vehicle = "SRV":
    							{if state.stimme_srv:
    								{set response to:
    								Das Schiff "{event.ship}" des Piloten {slice(event.name, 10)} mit Rang 
    								"{event.rank}" hat den Status "{legalstatus}" und {char(10)}{char(13)}ist damit ein legitiemes Ziel.}
    								{_ sorry für die falsche Schreibweise ! }
									{Voice(response, state.stimme_srv)}
								|else:
									Das Schiff "{event.ship}" des Piloten {slice(event.name, 10)} mit Rang 
    								"{event.rank}" hat den Status "{legalstatus}" und {char(10)}{char(13)}ist damit ein legitiemes Ziel.}
    						|elif status.vehicle = "Fighter":
    							{if state.stimme_fighter:
    								{set response to:
    								Das Schiff "{event.ship}" des Piloten {slice(event.name, 10)} mit Rang 
    								"{event.rank}" hat den Status "{legalstatus}" und {char(10)}{char(13)}ist damit ein legitiemes Ziel.}
									{Voice(response, state.stimme_srv)}
								|else:
									Das Schiff "{event.ship}" des Piloten {slice(event.name, 10)} mit Rang 
    								"{event.rank}" hat den Status "{legalstatus}" und {char(10)}{char(13)}ist damit ein legitiemes Ziel.}
							|else:
    							Das Schiff "{event.ship}" des Piloten {slice(event.name, 10)} mit Rang 
    							"{event.rank}" hat den Status "{legalstatus}" und {char(10)}{char(13)}ist damit ein legitiemes Ziel.
    						}
    					}
    		    		{if len(wanted_list) = 0: {set sep to ""} |else: {set sep to "+="}}
    		        	{set wanted_list to cat(wanted_list, [event.name])}
    		        	{SetState('eddi_context_wanted_list', join(wanted_list, sep))}
    		        |elif legalstatus = "Sauber": 
    						{if status.vehicle = "SRV":
    							{if state.stimme_srv:
    								{set response to:
									{OneOf("Ein unbescholtener Kommander.", "Er ist sauber.", 
									"Der hat kein Dreck am Stecken", "Puh, wie 	langweilig, sauber.",
									"Ein vorbildlicher Pilot.", "Das Sparschwein bleibt auf Diät.", 
									"Außer Spesen nix gewesen.")}
									{Voice(response, state.stimme_srv)}}
								|else:
									{OneOf("Ein unbescholtener Kommander.", "Er ist sauber.", 
									"Der hat kein Dreck am Stecken", "Puh, wie langweilig, sauber.",
									"Ein vorbildlicher Pilot.", "Das Sparschwein bleibt auf Diät.", 
									"Außer Spesen nix gewesen.")}}
    						|elif status.vehicle = "Fighter":
    							{if state.stimme_fighter:
    								{set response to:
									{OneOf("Ein unbescholtener Kommander.", "Er ist sauber.", 
									"Der hat kein Dreck am Stecken", "Puh, wie langweilig, sauber.",
									"Ein vorbildlicher Pilot.", "Das Sparschwein bleibt auf Diät.", 
									"Außer Spesen nix gewesen.")}
									{Voice(response, state.stimme_srv)}}
								|else:
									{OneOf("Ein unbescholtener Kommander.", "Er ist sauber.", 
									"Der hat kein Dreck am Stecken", "Puh, wie langweilig, sauber.",
									"Ein vorbildlicher Pilot.", "Das Sparschwein bleibt auf Diät.", 
									"Außer Spesen nix gewesen.")}}
							|else:
									{OneOf("Ein unbescholtener Kommander.", "Er ist sauber.", 
									"Der hat kein Dreck am Stecken", "Puh, wie langweilig, sauber.",
									"Ein vorbildlicher Pilot.", "Das Sparschwein bleibt auf Diät.", 
									"Außer Spesen nix gewesen.")}
    						}				
        	    	} 
				}	
    		}
		}
	}
}

{_
bounty          - The bounty being offered by system authorities for destruction of the ship 
faction         - The faction of the pilot 
hullhealth      - The health of the hull 
legalstatus     - The legal status of the pilot 
name            - The name of the pilot 
power           - The aligned power of the pilot (if player is pledged) 
rank            - The rank of the pilot 
scanstage       - the stage of the ship scan (e.g. 0, 1, 2, or 3) 
shieldhealth    - The health of the shields 
ship            - the model of the ship 
subsystem       - The subsystem targeted 
subsystemhealth - The health of the subsystem targeted 
targetlocked    - True when a ship has been targeted. False when a target has been lost/deselected 
_}

Ship targeted.defaultValue:
{_ Ship targeted }
{_ Triggered when the player selects a non-Thargoid target }

{_ event Parameters:   targetlocked - bool (ie false when losing target) _}
{_ If target locked:   name (of ship), scanstage (ie 0, 1, 2, 3) _}
{_ If Scan stage >= 1: name (of pilot), rank (of pilot) _}
{_ If scan stage >= 2: shieldhealth, hullhealth (both 0-100) _}
{_ If scan stage >= 3: faction, power (if player is pledged to a power), _}
{_                     legalstatus (ie clean, wanted, etc), bounty (if applicable), _}
{_                     subsystem (ie FSD, power plant, etc), subsystemhealth (0-100) _}


{_ Context }
{SetState('eddi_context_last_subject', 'ship')}
{SetState('eddi_context_last_action', 'targeted')}

{if environment = "Supercruise":
    {_ If we're in supercruise, check whether we are carrying a Frame Shift Drive Interdictor}
    {for compartment in ship.compartments:
        {if compartment.module.invariantName = "Frame Shift Drive Interdictor":
            {set hasInterdictor to true}
        }
    }
}

{if environment = "Normal Space" || hasInterdictor:
    {if event.targetlocked && event.scanstage > 2 && !event.subsystem:
        {set legalstatus to event.legalstatus}
        {if legalstatus = "Wanted" || legalstatus = "Wanted Enemy" || legalstatus = "Warrant":
            {set wanted_list to split(state.eddi_context_wanted_list, "+=")}
            {if find(wanted_list, event.name) < 0:
                {event.name} {OneOf("has a bounty", "is fair game", "is a legitimate target")}.
                {if len(wanted_list) = 0: {set sep to ""} |else: {set sep to "+="}}
                {set wanted_list to cat(wanted_list, [event.name])}
                {SetState('eddi_context_wanted_list', join(wanted_list, sep))}
            }    
        }
    }
}

Ship transfer initiated.script:
{Log("EVENT: Ship transfer initiated")}

{SetState('eddi_context_last_subject', 'ship transfer initiated')}
{SetState('eddi_context_last_event', 'ship delivered initiated')}

Ihr Schiff vom Typ {set translate to event.ship} {F("Translate Ship Names")} wird
{if event.price: zu einem Preis von {Humanise(event.price)} credditts}
{if (event.system): von {(event.system)}} {OneOf("transferiert", "übersandt", "überführt")}.

{set t_remaining to event.time}

{if t_remaining:
   {Pause(500)}
   {char(10)}{char(13)}Erwartete Ankunft in:

   {if t_remaining >= (60*60*24):
      {set t_days to floor(t_remaining/(60*60*24))}
      {if t_days = 1: einen Tag, |else: {t_days} Tagen,}
      {set t_remaining to t_remaining-(t_days*(60*60*24))}
   }
   {if t_remaining >= 3600:
      {set t_hours to floor(t_remaining/3600)}
      {if t_hours = 1: eine Stunde |else: {t_hours} Stunden,}
      {set t_remaining to t_remaining-(t_hours*(60*60))}
   }
   {if t_remaining >= 60:
      {if t_days || t_hours: und}
      {set t_minutes to round(t_remaining/(60),0)}
      {if t_minutes = 1: eine Minute |else: {t_minutes} Minuten.}
   }
}

{_ 
distance 		- The distance that the transferred ship needs to travel, in light years 
phoneticname 	- The phonetic name of the ship that is being transferred 
price 			- The price of transferring the ship 
ship 			- The (invariant) ship model that is being transferred 
shipid 			- The ID of the ship that is being transferred 
system 			- The system from which the ship is being transferred 
time 			- The time in seconds to complete transferring the ship
_}

Ship transfer initiated.defaultValue:
{ShipName(event.shipid, event.ship)} {OneOf("on its way","being transferred", "transfer initiated", "being transported", "being transferred")} from {P(event.system, "starsystem")}

{if event.transfercost: for a cost of {Humanise(event.price)} credits.}

{set t_remaining to event.time}

{if t_remaining:

    {Pause(1000)}
    Expected arrival in:

    {if t_remaining > (60*60*24):
        {set t_days to floor(t_remaining/(60*60*24))}
        {if t_days > 1:
            {t_days} days,
        |else:
            1 day,
        }
        {set t_remaining to t_remaining-(t_days*(60*60*24))}
    }

    {if t_remaining > 3600:
        {set t_hours to floor(t_remaining/3600)}
        {if t_hours > 1:
            {t_hours} hours,
        |else:
            1 hour,
        }
        {set t_remaining to t_remaining-(t_hours*(60*60))}
    }

    {if t_remaining > 60:
        {if t_days || t_hours: and}
        {set t_minutes to round(t_remaining/(60),0)}
        {if t_minutes > 1:
            {t_minutes} minutes.
        |else:
            1 minute,
        }
    }
}

