Surface signals detected.script:
{Log("EVENT: Surface signals detected / FSSBodySignals")}

{_ Context _}
{_ Used to remove initial pause in mapping script if signals are found. _}
{SetState('eddi_context_surfacesignals', true)}

{_ SetState('eddi_context_body_system', event.systemname)}
{_ SetState('eddi_context_body_name', event.bodyname)}
{_ SetState('eddi_context_body_shortname', event.shortname)}
{_ SetState('eddi_context_body_bodytype', event.bodytype)}

{_ set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system) - immer ein Körper zu spät}
{_ set reportbody to BodyDetails(body.bodyname, system.systemname) - geht nicht}

{set reportbody to BodyDetails(GetState('eddi_context_body_name'), GetState('eddi_context_body_system'))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState('eddi_context_body_name'))}
}

{_ Teil für die Ansage am Ende von "Body report2/3" _____________________________________ }

{set surfacesignals to []}
{for signal in event.surfacesignals: 
    {set surfacesignals_desc to: 
        {if signal.source = "Anderes Oberflächensignal" : 
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "unbekanntes Oberflächensignal"}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "unbekannte Oberflächensignale"}
        	}
        |elif signal.source = "Geologisches Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Geologisches Signal"} {_ Geologisches Oberflächensignal}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Geologische Signale"} {_ Geologische Oberflächensignale}
        	}
        	
        	{set geo_desc to:{signal_amount} {signal_source}}
        	{set anzahl_geo to:{signal_amount}}
        	
        |elif signal.source = "Unnormales Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Unnormales Oberflächensignal"}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Unnormale Oberflächensignale"}
        	}	
        |elif signal.source = "Menschliches Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Menschliches Oberflächensignal"}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Menschliche Oberflächensignale"}
        	}
        |elif signal.source = "Biologisches Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Bio-Signal"} {_ Biologisches Oberflächensignal}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Bio-Signale"} {_ Biologische Oberflächensignale"}
        	}
        	
        	{set bio_desc to:{signal_amount} {signal_source},}
        	{set anzahl_bio to:{signal_amount}}
        	
        |elif signal.source = "Thargoiden Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Thargoiden Oberflächensignal"}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Thargoiden Oberflächensignale"}
        	}
        |elif signal.source = "Guardian Oberflächensignal":
        	{if signal.amount = 1:
        		{set signal_amount to "ein"}
        		{set signal_source to "Guardian Oberflächensignal"}
        	|else: 
        		{set signal_amount to signal.amount}
        		{set signal_source to "Guardian Oberflächensignale"}
        	}
        }
        {signal_amount} {signal_source} {_ auf event.bodyname},
    }
    {set surfacesignals to cat(surfacesignals, [surfacesignals_desc])}
}
{set var_join to join(surfacesignals, "/")}
{SetState("surfacesignals_list", var_join)}

{SetState("desc_geo", geo_desc)}
{SetState("desc_bio", bio_desc)}

{SetState("anzahl_geo", anzahl_geo)}
{SetState("anzahl_bio", anzahl_bio)}


{_ dump [AnzahlSignale,SignalTyp] - zum Auslesen der Variablen}

{_
bodyname 				- The body where surface signals were detected 
detectionType 			- The signal detection type (either 'FSS' or 'SAA' 
surfacesignals 			- A list of signals (as objects) 
surfacesignals[<index>].amount 
surfacesignals[<index>].source

event.surfacesignals
