|else:
}

{Pause(500)}
{if !reportSystem.name || reportSystem.name = "": Nach welchem System fragst Du?
|else:
    Das System {reportSystem.name} {unb_artikel}

    {if reportSystem.population && reportSystem.population > 0:
        {if   reportSystem.security = "High": 		{OneOf("sicher{endung}", "gut geregelt{endung}")}
        |elif reportSystem.security = "Low":		{OneOf("ziemlich unsicher{endung}", "wenig geschützt{endung}")}
        |elif reportSystem.security = "Anarchy":	unsicher{endung}
        |else:
        }
        
        {  if reportSystem.allegiance = "Federation"  || reportSystem.allegiance = "Föderation" : föderal{endung}
        |elif reportSystem.allegiance = "Empire"      || reportSystem.allegiance = "Imperium"   : imperial{endung}
        |elif reportSystem.allegiance = "Independent" || reportSystem.allegiance = "Unabhängig" : unabhängig{endung}
        |elif reportSystem.allegiance = "Allianz"                                               : aliirt{endung}
        |else: {reportSystem.allegiance}{endung}
        }
        {if reportSystem.government = "Kommunismus" || reportSystem.government = "Kommunistisch" : kommunistisches System,
        |elif reportSystem.government != "None": {reportSystem.government}, 
        |else: System,
        }
        {char(10)}{char(13)}{OneOf("mit", "mit einer Bevölkerung von", "Heimat von")}
        {set value to SystemDetails(system.systemname).population} {F("HuVal")} {OneOf("Menschen", "Bewohnern", "Einwohnern", "Bürgern")}
        {if reportSystem.powerState.invariantName != "None":
            {if reportSystem.powerState.invariantName = "Home System":
                , und ist die Heimat von {set translate to reportSystem.power} {F("Translate Power")}
            |elif reportSystem.powerState.invariantName = "Control":
                , und {OneOf("ist alliert mit", "wird kontrolliert von")} {set translate to reportSystem.power} {F("Translate Power")}
            |elif reportSystem.powerState.invariantName = "Exploited":
                , und ist {OneOf("verbunden mit", "im Einfluss von")} {set translate to reportSystem.power} {F("Translate Power")}
            |elif reportSystem.powerState.invariantName = "Contested":
                , und wird von mehreren Mächten umkämpft
            }
        }.

        {if reportSystem.faction:
            Die Gruppe {reportSystem.faction} ist die {OneOf("kontrollierende", "lokale", "unmittelbare")}
            {OneOf("Power,", "Fraktion,", "Kraft,")}}
        {if len(reportSystem.stations) = 0:
            Hier gibt es keine Stationen
        |else:
            {set systemStations to []}
            {if len(reportSystem.orbitalstations) = 1:
                {set orbitalStationDesc to:Es gibt {OneOf("eine einzelne", "nur eine", "gerade eine", "eine")} Orbitalstation}
            |elif len(reportSystem.orbitalstations) > 1:
                {set orbitalStationDesc to:Es gibt {len(reportSystem.orbitalstations)} Orbitalstationen}
            }
            {if len(reportSystem.planetarystations) = 1:
                {set planetaryStationDesc to:Es gibt {OneOf("eine einzelne", "nur eine", "gerade eine", "eine")}
                {OneOf("Oberflächenstation", "Bodenstation")}}
            |elif len(reportSystem.planetarystations) > 1:
                {set planetaryStationDesc to:Es gibt {len(reportSystem.planetarystations)}
                {OneOf("Oberflächenstationen", "Bodenstationen")}}
            }
            {if len(reportSystem.carriersignalsources) = 1:
                {set fleetCarrierDesc to:
                    {if len(reportSystem.orbitalstations) = 0 && len(reportSystem.planetarystations) = 0: Es gibt}
                    {OneOf("einen einzelnen", "nur einen", "gerade einen", "einen")} {OneOf("Flieht Carrier", "Träger", "Carrier", "Flottenträger")}
                }
            |elif len(reportSystem.carriersignalsources) > 1:
                {set fleetCarrierDesc to:            
                    {if len(reportSystem.orbitalstations) = 0 && len(reportSystem.planetarystations) = 0: Es gibt}
                    {len(reportSystem.carriersignalsources)} {OneOf("Flieht Carrier", "Träger", "Carrier", "Flottenträger")}
                }
            }
            {if orbitalStationDesc:{set systemStations to cat(systemStations, [orbitalStationDesc])}
            }
            {if planetaryStationDesc:{set systemStations to cat(systemStations, [planetaryStationDesc])}
            }
            {if fleetCarrierDesc:{set systemStations to cat(systemStations, [fleetCarrierDesc])}
            }
            {List(systemStations)}
        }
        {OneOf("in diesem", "im aktuellen")} System
    |else:
       ist unbewohnt
   }.
}

