System scan complete.script:
{Log("EVENT: System scan complete")}

{SetState('object_ersterfassung', false)}
{SetState('eddi_context_last_action', 'system_scan_complete')}

{if SystemDetails(event.systemname) = "":
    {set reportSystem to SystemDetails(system.systemname)}
|else:
    {set reportSystem to SystemDetails(event.systemname)}
}

{_ Preferences _}
{set reportGreenSystems to true}    {_ if true, reports systems containing all fsd synthesis elements. _}
{set reportGoldSystems to true}     {_ if true, reports systems containing all surface prospectable elements. _}
{set reportMaterials to true}       {_ If true, recommends material collection locations using the `System materials report` 
									after we have data on all system bodies and when the commander has an SRV _}

{if reportSystem:
    {set scannedBodies to 0} 
    {set alreadydiscovered to 0}
    {set nondiscovered to 0}
    {set alreadymapped to 0}
    {set body_stern to 0}

	{_ for body in reportSystem.bodies:}
    {for body in SystemDetails(system.systemname).bodies:
        {if body.bodytype = "Mond" || body.bodytype = "Planet": 
            {if body.alreadymapped: {set alreadymapped to alreadymapped + 1}}
            {if body.alreadydiscovered: {set alreadydiscovered to alreadydiscovered + 1} 
            |elif !body.alreadydiscovered: {set nondiscovered to nondiscovered + 1} }
        }
    	{if body.scanned: {set scannedBodies to scannedBodies + 1}}
    	{if body.bodytype = "Stern": {set body_stern to body_stern + 1}
        	{  if body.mainstar && body.alreadydiscovered = true && reportSystem.scannedbodies = reportSystem.totalbodies : {set system_entdeckt to 2}
			|elif body.mainstar && body.alreadydiscovered = false && reportSystem.scannedbodies = 0: {set system_entdeckt to 0}
           	|elif body.mainstar && body.alreadydiscovered && body.scanned : {set system_entdeckt to 1}     	
        	}
    	}	
	} 
}

{Pause(500)}

{OneOf("Alles erfasst", "Der System Scan ist abgeschlossen", "System gescannt", "System komplett gescannt")}, {F('Honorific')}.

{Pause(800)}

{set zu_mappen_first to reportSystem.totalbodies - alreadymapped - body_stern}

{if nondiscovered: Glück Wunsch! Wir haben 
	{if nondiscovered = 1: einen |else: {nondiscovered}} Körper als erster entdeckt!} 
	
{if zu_mappen_first > 0 : 
	Wir können noch
	{if zu_mappen_first = 1 : {OneOf("einen Körper", "ein Objekt")} 
