System state report.script:
{Log("Fktn.: System state report")}

{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{set reportSystem to SystemDetails(system.systemname)}

{if !reportSystem.name || reportSystem.name = "": Welches System ist gemeint ?

|else:

    {_ Report any ongoing conflicts _}
    {set conflictfactions to []}
    {if reportSystem.conflicts != null:
        {for conflict in reportSystem.conflicts:
            Fraktion {conflict.faction1}
            {if conflict.status = "active":
            |elif conflict.status = "pending": 	wird bald sein
            |else: 								erholen sich nach
            }
            {if conflict.faction1dayswon > conflict.faction2dayswon:
               {if conflict.margin >= 3: 	dominieren
               |elif conflict.margin = 2: 	gewinnen
               |else: 						sind voraus
               }
            |elif conflict.faction2dayswon > conflict.faction1dayswon:
               {if conflict.margin >= 2: 	verlieren
               |else: 						liegen zurück
               }
            |else: 							liegen gleich auf
            }
            {_ if StartsWithVowel(conflict.state): an else: a }
            {if conflict.state = "Wahlen": bei den Wahlen gegen die Fraktion {conflict.faction2}
            |else: mit {conflict.faction2}
            } 
            {if conflict.stake: für die Kontrolle über die Station {conflict.stake}
            }
            .
            {set conflictfactions to cat(conflictfactions, [conflict.faction1])}
            {set conflictfactions to cat(conflictfactions, [conflict.faction2])}
        }
    }

    {_ List our non-conflict faction states _}
    {set otherstates to []}
    {for faction in reportSystem.factions:
        {if faction.influence > 0 && find(conflictfactions, faction.name) = -1:
            {for presence in faction.presences:
                {if presence.systemName = reportSystem.name:
                    {if presence.FactionState.invariantName != "None":

                        {_ Note our controlling faction, if present in our `otherstates` list. _}
                        {if faction.name = reportSystem.faction:
                            {set factions to token(factions, "", index, "controlling ")}
                        }

                        {_ Add the faction to our list _}
                        {if has(otherstates, presence.state):
                            {set factions to otherstates[presence.state])}
                            {set factions to cat(factions, ",", faction.name)}
                            {set otherstates to union(otherstates, [presence.state: factions])}
                        |else:
                            {set otherstates to union(otherstates, [presence.state: faction.name])}
                        }
                    }
                }
            }
        }
    }

    {_ Sort from most factions with each state to least factions with each state. _}
    {declare by_items(a, b) as:
        {return cmp(len(split(b, ",")), len(split(a, ",")))}
    }
    {sort(otherstates, by_items)}

    {_ Prepare summaries for each state _}
    {set statesummaries to []}
    {for state, factions in otherstates:

        {set factions to split(factions, ",")}

        {set statesummary to:
            {if len(factions) > 1:
                {List(factions)},
            |else:
                {factions[0]},
            }
            {if state = "Retreat" || state = "Rückzug":							befindet sich im Rückzug
            |elif state = "War" || state = "Krieg":								befindet sich im Krieg
            |elif state = "Boom" || state = "Aufschwung":						befindet sich im wirtschaftlichem Aufschwung
            |elif state = "Civil Liberty" || state = "Bürgerliche Freiheit":	befindet sich in eine Periode erhöhte bürgerliche Freiheiten zu genießen
            |elif state = "Civil Unrest" || state = "Unruhen":					erlebt zivile Unruhen
            |elif state = "Civil War" || state = "Bürgerkrieg":					befindet sich im Bürgerkrieg
            |elif state = "Famine" || state = "Hungersnot":						erlebt eine Hungersnot
            |elif state = "Lockdown" || state = "Abriegelung":					befindet sich im Lockdown
            |elif state = "Outbreak" || state = "Ausbruch":						versucht eine Krankheit einzudämmen
            |elif state = "Expansion" || state = "Expansion":					bereitet sich darauf vor, in ein anderes System zu expandieren
            |elif state = "Bust" || state = "Pleite":							ist finanziell ruiniert
            |elif state = "Election" || state = "Wahlen":						wählt eine neue Führung
            |elif state = "Investment" || state = "Investition":				investiert in die Infrastruktur
            |elif state = "Incursion" || state = "Eindringen":					wird von außerirdischen Kräften angegriffen
            |elif state = "Pirate Attack" || state = "Piratenangriff":			leidet unter Piratenangriffen
            |elif state = "Blight" || state = "Seuche":							leidet unter einer Seuche
            |elif state = "Drought" || state = "Dürre":							leidet unter einer Dürre
            |elif state = "Natural disaster" || state = "Naturkatastrophe":		kämpft mit einer Naturkatastrophe
            |elif state = "Infrastructure Failure" || state = "Ausfall der Infrastruktur": hat Probleme mit der Infrastruktur
            |elif state = "Terrorism" || state = "Terroranschlag":				muss einen Terroranschlag verarbeiten
            |elif state = "Public Holiday" || state = "Feiertag":				begeht einen Feiertag
            |elif state = "Thargoiden-Alarm":									hat Thargoiden-Alarm ausgerufen
            |elif state = "Thargoiden-Invasion":								steht unter einer Thargoiden-Invasion
            |elif state = "Unter Thargoiden-Kontrolle":							befindet sich unter Thargoiden-Kontrolle
            |elif state = "Thargoiden-Maelstrom":								wird von Thargoiden überrannt
            |elif state = "Post-Thargoiden-Wiederherstellung":					befindet sich im Aufbau nach einem Thargoidenangriff
            |else: {_ catch-all, worded to avoid deciding between a/an}
                befindet sich in einem Zustand {state}
            },
        }
        {set statesummaries to cat(statesummaries, [statesummary])}
    }

    {_ Describe our non-conflict states _}
    {if len(statesummaries) > 0:
        {Pause(100)}
        {List(statesummaries)}
    }

}

