                If not repulsed, the system {OneOf("will", "shall")} 
                {if thargoidWar.FailureState.invariantName = "Thargoid Controlled":
                    fall into complete Thargoid control
                |elif thargoidWar.FailureState.invariantName = "Thargoid Invasion":
                    {OneOf("come under assault", "be invaded", "suffer a full scale invasion", "suffer a full scale assault")}
                }
                {if thargoidWar.remainingDays != null:
                    within the next {thargoidWar.remainingDays} days
                }.
            }
        |elif thargoidWar.CurrentState.invariantName = "Thargoid Invasion":
            {if !thargoidWar.succeeded:
                Danger! Thargoid {OneOf("vessels", "interceptors")} are
                {OneOf("invading", "assaulting", "attempting to gain control of")} 
                this {OneOf("star system", "region")}.
                {if thargoidWar.FailureState.invariantName = "Thargoid Controlled":
                    If not repulsed, the system {OneOf("will", "shall")} 
                    fall into complete Thargoid control
                    {if thargoidWar.remainingDays != null:
                        within the next {thargoidWar.remainingDays} days
                    }.
                }
                {if thargoidWar.remainingPorts > 0:
                    {if thargoidWar.remainingPorts = 1: Just }
                    {thargoidWar.remainingPorts}
                    {if thargoidWar.remainingPorts > 1: ports remain |else: port remains }
                    under human control.
                }
            |else:
                This system is just beginning to recover after repulsing a 
                Thargoid {OneOf("invasion", "assault")}.
            }
        |elif thargoidWar.CurrentState.invariantName = "Thargoid Controlled":
            {if !thargoidWar.succeeded:
                This {OneOf("star system", "region")} is under 
                {OneOf("complete Thargoid control", "Thargoid occupation")}.
                Proceed with extreme caution.
            |else:
                {if thargoidWar.SuccessState.invariantName = "Post-Thargoid Recovery":
                    This system is just beginning to recover after 
                    being {OneOf("recovered", "recaptured", "wrested")}
                    from Thargoid control.
                }
            }
        |elif thargoidWar.CurrentState.invariantName = "Thargoid Maelstrom":
            A Thargoid Maelstrom is present in this 
            {OneOf("star system", "region")}. Proceed with extreme caution.
        |elif thargoidWar.CurrentState.invariantName = "Post-Thargoid Recovery":
            {if !thargoidWar.succeeded:
                This system is expected to complete its recovery 
                from Thargoid {OneOf("assault", "attack")}
                {if thargoidWar.remainingDays != null:
                    within the next {thargoidWar.remainingDays} days
                }.
            |else:
                This system is recovering from Thargoid {OneOf("assault", "attack")}.
            }
        }
        
    |else:
    
        {_ Report any ongoing human conflicts _}
        {set conflictfactions to []}
        {if reportSystem.conflicts != null:
            {for conflict in reportSystem.conflicts:
                {P(conflict.faction1, "faction")}
                {if conflict.status = "active":
                    are
                    {if conflict.faction1dayswon > conflict.faction2dayswon:
                        {if conflict.margin >= 3:
                            dominating
                        |elif conflict.margin = 2:
                            winning
                        |else:
                            ahead in
                        }
                    |elif conflict.faction2dayswon > conflict.faction1dayswon:
                        {if conflict.margin >= 2:
                            losing
                        |else:
                            trailing in
                        }
                    |else:
                        tied in
                    }
                |elif conflict.status = "pending":
                    will soon be in
                |else:
                    are {OneOf("recovering", "recuperating")} after
                }
                {if StartsWithVowel(conflict.state): an |else: a }
                {conflict.state}
                with 
                {P(conflict.faction2, "faction")}
                {if conflict.stake:
                   for control of {P(conflict.stake, "station")}
                }
                .
                {set conflictfactions to cat(conflictfactions, [conflict.faction1])}
                {set conflictfactions to cat(conflictfactions, [conflict.faction2])}
            }
