Touchdown.script:
{Log("EVENT: Touchdown")} 

{_ Context }
{SetState('eddi_context_last_subject', 'ship')}
{SetState('eddi_context_last_event', 'touchdown')}
{SetState('eddi_context_last_action', 'touchdown')}
{SetState('eddi_context_touchdown_body_name', event.bodyname)}

{_ Preferences _}
{set reportTemperature to 0}   {_ if truthy, report the external temperature (for on-foot expeditions) _}
{set reportTempScale to 1}     {_ Set to 0 for temperatures in Kelvin _}
                               {_ Set to 1 for temperatures in Celsius _}
                               {_ Set to 2 for temperatures in Fahrenheit _}

{_ Fetch from context _}
{set reportBody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_system_name"))}
{if !reportBody: {set reportBody to BodyDetails(event.bodyname, event.systemname)}}
{_ set reportBody to BodyDetails(event.bodyname, event.systemname) _}

{set surfacetemp to reportBody.temperature}
{set hazardousHighTemp to reportBody.temperature > 426}
{set hazardousLowTemp to reportBody.temperature < 150}

{if reportTempScale = 0:
    {set surfTemp to round(surfacetemp, 0)}
    {set tempScale to " Kelvin "}
|elif reportTempScale = 1:
    {set surfTemp to round(surfacetemp - 273.15 , 0)}
    {set tempScale to " Grad Celsius "}
|elif reportTempScale = 2:
    {set surfTemp to round((surfacetemp - 273.15) * 9 / 5 + 32 , 0)}
    {set tempScale to " Grad Fahrenheit "}
}

{if GetState('eddi_context_last_subject') != "location" :
    {if event.playercontrolled = true : Tatsch daun!
        {if reportBody.shortname && GetState('eddi_context_touchdown_body_name') != event.bodyname: 
            auf {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")},
        }
    |else:
        {ShipName()} {OneOf("ist gelandet", "hat aufgesetzt")}
        {if reportBody.shortname && GetState('eddi_context_touchdown_body_name') != event.bodyname: 
            auf {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")},
        }
    } 
	{OneOf("Motoren","Triebwerke","Antrieb")} {OneOf("Offline","deaktiviert","heruntergefahren")}. 
    {Pause(800)}
}

{if state.say_coordinates = 1:  
    {if reportCoordinates && type(event.latitude) != "void" && type(event.longitude) != "void" :
   	    {OneOf("Wir sind bei folgenden Koordinaten gelandet:","Die Koordinaten \{OneOf(\"sind:\",\"lauten:\")\}")} 
   	    {round(event.latitude, 2)} Grad Breite, {round(event.longitude, 2)} Grad Länge.   
    }
|elif state.say_coordinates = 2:
    {if ship.role = "Explation" || ship.role = "Erkundung":
        {if reportCoordinates && type(event.latitude) != "void" && type(event.longitude) != "void" :
           {OneOf("Wir sind bei folgenden Koordinaten gelandet:","Die Koordinaten \{OneOf(\"sind:\",\"lauten:\")\}")} 
           {round(event.latitude, 2)} Grad Breite, {round(event.longitude, 2)} Grad Länge.   
        }
    }     
|else:
}

{if reportTemperature:
    {_ Report dangerous surface temperatures for on-foot expeditions _}
    {if hazardousHighTemp || hazardousLowTemp:
        Warnung: Dieser {reportBody.bodytype} hat eine {OneOf("gefährlich", "bedrohlich", "bedenklich")}
        {if hazardousHighTemp: hohe |else: niedrige } {Occasionally(3, "durchschnittliche")}
        {OneOf("Oberflächen-", "Umgebungs-")} Temperatur von {surfTemp} {tempScale}.
    }
}

{_ von Darkcyde Skript }
{if event.playercontrolled:
    {F('Material Desired On Body')}
    {F("Exobiology Report")}
}

{_
bodyId              - The numeric ID of the body where the ship has touched down 
bodyname            - The name of the body where the ship has touched down 
latitude            - The latitude from where the ship has touched down 
longitude           - The longitude from where the ship has touched down 
multicrew           - True if the ship is belongs to another player 
nearestdestination  - The nearest location from where the ship has touched down 
playercontrolled    - True if the ship is controlled by the player 
systemAddress       - The numeric system address of the star system where the ship has touched down 
systemname          - The name of the star system where the ship has touched down 
taxi                - True if the ship is a transport (e.g. taxi or dropship)
_}

Touchdown.defaultValue:
{_ Preferences _}
{set reportCoordinates to 0}   {_ if truthy, report the latitude and longitude of the ship _}
{set reportFirstFootfall to 1} {_ if truthy, report whether a first foot fall can be registered (for on-foot expeditions) _}
{set reportTemperature to 1}   {_ if truthy, report the external temperature (for on-foot expeditions) _}
{set reportTempScale to 0}     {_ Set to 0 for temperatures in Kelvin _}
                               {_ Set to 1 for temperatures in Celsius _}
                               {_ Set to 2 for temperatures in Fahrenheit _}
                                   
{_ Fetch from context _}
{set reportBody to BodyDetails(state.eddi_context_body_name, state.eddi_context_system_address)}
{if !reportBody:
    {set reportBody to BodyDetails(event.bodyname, event.systemname)}
}

{if state.eddi_context_last_subject != "location":
    {if event.playercontrolled = true:
        Touchdown
        {if reportBody.shortname && state.eddi_context_touchdown_body_name != event.bodyname: 
            on {reportBody.bodytype} {P(reportBody.shortname, "body")}
        }.
    |else:
        {ShipName()} has touched down
        {if reportBody.shortname && state.eddi_context_touchdown_body_name != event.bodyname: 
            on {reportBody.bodytype} {P(reportBody.shortname, "body")}
        }.
    }
    {Pause(1000)}
}

{if reportCoordinates && type(event.latitude) != "void" && type(event.longitude) != "void" :
   {Occasionally(2, "Current")} Coordinates are
   {round(event.latitude, 2)} degrees latitude, 
   {round(event.longitude, 2)} degrees longitude.
}

{if reportFirstFootfall && odyssey && event.canfirstfootfall = true:
    There is no record of any first foot fall
    {OneOf(
        "on this world.",
        "here."
    )}
}

{if reportTemperature && odyssey && state.eddi_context_touchdown_body_name != event.bodyname:
    {_ Convert to desired temperature scale _}
    {if reportTempScale = 0:
        {set surfTemp to round(reportBody.temperature, 0)}
        {set tempScale to " Kelvin "}
    |elif reportTempScale = 1:
        {set surfTemp to round(reportBody.temperature - 273.15, 0)}
        {set tempScale to " degrees Celsius "}
    |elif reportTempScale = 2:
        {set surfTemp to round((reportBody.temperature - 273.15) * 9 / 5 + 32, 0)}
        {set tempScale to " degrees Fahrenheit "}
    }

    {_ Report dangerous surface temperatures for on-foot expeditions _}
    {set hazardousHighTemp to reportBody.temperature > 426}
    {set hazardousLowTemp to reportBody.temperature < 150}
    {if hazardousHighTemp || hazardousLowTemp:
        Caution: This {reportBody.bodytype} has a 
        {OneOf("hazardously", "dangerously")}
        {if hazardousHighTemp: high |else: low }
        {Occasionally(3, "average")}
        {OneOf("surface", "external")}
        temperature of {surfTemp} {tempScale}.
    }
}

{_ Context }
{SetState('eddi_context_last_subject', 'ship')}
{SetState('eddi_context_last_action', 'land')}
{SetState('eddi_context_touchdown_body_name', event.bodyname)}

