    |else: {shortname},}
|elif len(shortname) = 6:
    {  if find(shortname, "g") = 5  : {set shortname to token(shortname, " ", 2, "gee")} {shortname}
    |elif find(shortname, "g") = 3  : {set shortname to token(shortname, " ", 1, "gee")} {shortname}
    |else: {shortname},}
|elif len(shortname) = 7:
    {  if find(shortname, "g") = 6  : {set shortname to token(shortname, " ", 2, "gee")} {shortname}
    |else: {shortname},}
|else: {_ shortname}
}

