Translate Stationsnamen.script:
{SetState("station_name", "")}
{_ set translate to "Duxfort Station"}
{_ set translate to "Station ABC"}
{_ set translate to "Rescue Ship Cornwallis"}
{_ set translate to "$EXT_PANEL_ColonisationShip:#index=1;"}
{_ set translate to "Harald's Base"}
{_ set translate to "Pipis' Zuhause"}  {_ Lending, Habb, Inherritenz, Hide, Base}



{if find(join(slice(split(translate, " "),0,1)), "Rescue") = 0:
    {if find(join(slice(split(translate, " "),1,1)), "Ship") = 0:
        {set name to token(translate, " ", 2)}
        {set translate to cat("Rettungsschiff ", name)}
        {translate}
    }
|elif find(join(slice(split(translate, " "),1,1)), "Station") = 0:
    {set station_name to join(slice(split(translate, " ",),0,1))}
    {SetState("station_name", cat("Station ", station_name))}
    {_ station_name} {_ SetState("station_name", station_name)}
|elif find(join(slice(split(translate, " "),1,1)), "Beacon") = 0:
    {set station_name to join(slice(split(translate, " ",),0,1))}
    {SetState("station_name", cat(station_name, " Bieken"))}
    {_ state.station_name}
|elif find(join(slice(split(translate, " "),1,1)), "Hub") = 0:
    {set station_name to join(slice(split(translate, " ",),0,1))}
    {SetState("station_name", cat(station_name, " Habb"))}
    {_ state.station_name}
|elif find(join(slice(split(translate, " "),1,1)), "Hide") = 0:
    {set station_name to join(slice(split(translate, " ",),0,1))}
    {SetState("station_name", cat(station_name, "," "Heid"))}
    {_ state.station_name}
|elif find(join(slice(split(translate, " "),1,1)), "Base") = 0:
    {set station_name to join(slice(split(translate, " ",),0,1))}
    {SetState("station_name", cat(station_name, "," "Bäis"))}
    {_ state.station_name}    
|else:
    {SetState("station_name", "")}
    {set new_translate to translate}
    {SetState("station_name", new_translate)}
    {_ translate}
}

{_ GetState("station_name")}

{_ set translate to "Terminal"}
{_ set translate to join(split(translate, "Terminal"), "Törminel"))} {_ translate}

