	{_ Ship model, Fighter and SRV Check _}
	{_ F("Hangar check")}

	{_ F("Crew assigned")}
	{_ F("Launchbay report")}
	
	{Pause(500)}
	{if state.keine_zielansage = 1:
	|else:
